import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { Card, Avatar, Input, Typography, Button, Divider } from 'antd';
import 'antd/dist/reset.css';
import './App.css'

const { Search } = Input;
const { Text } = Typography;
const { Meta } = Card;

const client = new W3CWebSocket('ws://127.0.0.1:8000');

export default class App extends Component {

  state = {
    userName: '',
    isLoggedIn: false,
    messages: []
  }
  count = 0;

  onButtonClicked = (value) => {
    client.send(JSON.stringify({
      type: "message",
      msg: value,
      user: this.state.userName
    }));
    this.setState({ searchVal: '' })
  }
  componentDidMount() {
    client.onopen = () => {
      console.log('WebSocket Client Connected');
    };
    client.onmessage = (message) => {
      const dataFromServer = JSON.parse(message.data);
      console.log('got reply! ', dataFromServer);
      if (dataFromServer.type === "message") {
        this.setState((state) =>
        ({
          messages: [...state.messages,
          {
            msg: dataFromServer.msg,
            user: dataFromServer.user
          }]
        })
        );
      }
    };
  }
  render() {
    return (
      <div className="main" id='wrapper'>
        {this.state.isLoggedIn ?
          <div>
            <div className="title">
              <Text id="main-heading" type="secondary" style={{ fontSize: '36px' }}>Megaclicker</Text>
            </div>
            <div style={{ display: 'flex', flexDirection: 'column', paddingBottom: 50 }} id="messages">
              <Divider style={{ width: 300, paddingTop: 20 }}>
                <Button style={{ width: 300, height: 100 }}>awawaw</Button>
              </Divider>

              {this.state.messages.map(message =>
                <Card key={message.msg} style={{ width: 300, margin: '16px 4px 0 4px', alignSelf: this.state.userName === message.user ? 'flex-start' : 'flex-start' }} loading={false}>
                  <Meta
                    avatar={
                      <Avatar style={{ color: '#f56a00', backgroundColor: '#fde3cf' }}>{message.user[0].toUpperCase()}</Avatar>
                    }
                    title={message.user + ":"}
                    description={message.msg}
                  />
                </Card>
              )}

            </div>
            <div className="bottom">
              <Search
                placeholder="input message and send"
                enterButton="Send"
                value={this.state.searchVal}
                size="large"
                onChange={(e) => this.setState({ searchVal: e.target.value })}
                onSearch={value => this.onButtonClicked(value)}
              />
            </div>
          </div>
          :
          <div style={{ padding: '200px 40px' }}>
            <Search
              placeholder="Enter Username"
              enterButton="Login"
              size="large"
              onSearch={value => this.setState({ isLoggedIn: true, userName: value })}
            />
          </div>
        }
      </div>
    )
  }
}

ReactDOM.render(<App />, document.getElementById('root'));
